function editFigureDialog(varargin)
%EDITFIGUREDIALOG opens a small GUI enabling users to set plot parameters
% this funtion creates an instance of a small GUI with plot configuration
% abilities. It is called in disableMenuBar.m by the hEdit handle, which is
% created whenever a Planview or Mean Cross Section plot is spawned in VMT.
% 
% Created by: Frank L. Engel, USGS
% Last modified: 2014/07/08

% Grable the handle to the current figure
figure_handle = varargin{3};

% Size and margins of the GUI
w = 300;
h = 300;
dx = 10;
dy = 15;

% Pull info from the current figure
figprops = get(figure_handle);
axes_handle = gca;
title_handle = get(gca,'title');

% Defaults for Presentation Stlye Figure
% --------------------------------------
BkgdColor   = 'black';
AxColor     = 'white'; % Also lines and font color
FigColor    = 'black'; % [0.3 0.3 0.3]
Font        = 'Helvetica';
FntSize     = 14;
GridOO      = 'off';  % Grid lines off

% Create/pull paramters from the active figure
diagram_params.figure_handle        = figure_handle;
diagram_params.plot_color           = get(figure_handle,'Color');
diagram_params.title_string         = get(title_handle,'String');
diagram_params.title_font           = get(title_handle,'FontName');
diagram_params.title_size           = get(title_handle,'FontSize');
diagram_params.title_color          = get(title_handle,'Color');
diagram_params.axes_font            = get(axes_handle,'FontName');
diagram_params.axes_font_color      = get(axes_handle,'XColor');  % X axes font color (assume/enforce the same for all)
diagram_params.axes_size            = get(axes_handle,'FontSize');
diagram_params.axes_color           = get(axes_handle,'Color');   % Fill color
diagram_params.axes_grid            = get(axes_handle,'XGrid');
diagram_params.background_color     = get(figure_handle,'Color');
diagram_params.show_file_info       = 'off';
diagram_params.all_fonts            = listfonts;
diagram_params.selected_font_idx    = find(ismember(diagram_params.all_fonts,diagram_params.title_font));

% Create a backup of the parameters representing the defaults
original_params.figure_handle        = figure_handle;
original_params.plot_color           = BkgdColor;
original_params.title_string         = get(title_handle,'String');
original_params.title_font           = Font;
original_params.title_size           = FntSize;
original_params.title_color          = AxColor;
original_params.axes_font            = Font;
original_params.axes_font_color      = AxColor;  % X axes font color (assume/enforce the same for all)
original_params.axes_size            = FntSize;
original_params.axes_color           = BkgdColor;   % Fill color
original_params.axes_grid            = GridOO;
original_params.background_color     = BkgdColor;
original_params.show_file_info       = 'on';
original_params.all_fonts            = listfonts;
original_params.selected_font_idx    = find(ismember(original_params.all_fonts,original_params.title_font));

% Build the GUI (not visible yet)
the_color = get(0,'factoryUipanelBackgroundColor');
handles.Figure = figure('Name', 'Edit Plot Congifuration', ...
    'Color',the_color, ...
    'NumberTitle','off', ...
    'HandleVisibility','callback', ...
    'WindowStyle','modal', ...
    'MenuBar','none', ...
    'ToolBar','none', ...
    'Units','pixels', ...
    'Position',[0 0 w h], ...
    'Resize','on', ...
    'Visible','off');
handles.TitlePanel = uipanel('Parent',handles.Figure, ...
    'Title', 'Title', ...
    'Units','pixels', ...
    'Position',[dx 0.7*h w-2*dx 0.28*h]);
handles.TitleString = uicontrol('Style','edit', ...
    'Parent',handles.TitlePanel, ...
    'Max',2,...
    'String',diagram_params.title_string, ...
    'Units','pixels', ...
    'BackgroundColor','white',...'
    'HorizontalAlignment','left',...
    'Position',[dx 0.12*h 0.76*w 30]);
handles.TitleFont = uicontrol('Style','popupmenu',...
    'Parent',handles.TitlePanel, ...
    'String',diagram_params.all_fonts, ...
    'Value',diagram_params.selected_font_idx,...
    'Units','pixels', ...
    'BackgroundColor','white',...'
    'HorizontalAlignment','left',...
    'Position',[dx 0.03*h 0.4*w 22]);
handles.TitleSize = uicontrol('Style','edit',...
    'Parent',handles.TitlePanel, ...
    'Max',1,...
    'String',diagram_params.title_size, ...
    'Units','pixels', ...
    'BackgroundColor','white',...'
    'HorizontalAlignment','center',...
    'Position',[0.5*w 0.03*h 22 22]);
handles.TitleSizeLabel = uicontrol('Style','text',...
    'Parent',handles.TitlePanel, ...
    'String','Font size (pt)', ...
    'Units','pixels', ...
    'HorizontalAlignment','center',...
    'Position',[0.58*w 0.025*h 65 22]);
handles.PlotColorPicker = uicontrol('Style','pushbutton',...
    'Parent',handles.TitlePanel, ...
    'CData',FloodFillIcon(),...
    'Tooltip', 'Choose Plot Background Color',...
    'Position',[0.83*w 0.135*h 20 20]);
handles.TitleColorPicker = uicontrol('Style','pushbutton',...
    'Parent',handles.TitlePanel, ...
    'CData',FontColorIcon(),...
    'Tooltip', 'Choose Title Font Color',...
    'Position',[0.83*w 0.035*h 20 20]);
handles.AxesPanel = uipanel('Parent',handles.Figure, ...
    'Title', 'Axes', ...
    'Units','pixels', ...
    'Position',[dx 0.4*h w-2*dx 0.28*h]);
handles.AxesGridOnOff = uicontrol('Style','checkbox', ...
    'Parent',handles.AxesPanel, ...
    'String','Gridlines on or off?', ...
    'Units','pixels', ...
    'Position',[dx 0.12*h 0.86*w 30]);
handles.AxesFont = uicontrol('Style','popupmenu',...
    'Parent',handles.AxesPanel, ...
    'String',diagram_params.all_fonts, ...
    'Value',diagram_params.selected_font_idx,...
    'Units','pixels', ...
    'BackgroundColor','white',...'
    'HorizontalAlignment','left',...
    'Position',[dx 0.03*h 0.4*w 22]);
handles.AxesSize = uicontrol('Style','edit',...
    'Parent',handles.AxesPanel, ...
    'Max',1,...
    'String',diagram_params.title_size, ...
    'Units','pixels', ...
    'BackgroundColor','white',...'
    'HorizontalAlignment','center',...
    'Position',[0.5*w 0.03*h 22 22]);
handles.AxesSizeLabel = uicontrol('Style','text',...
    'Parent',handles.AxesPanel, ...
    'String','Font size (pt)', ...
    'Units','pixels', ...
    'HorizontalAlignment','center',...
    'Position',[0.58*w 0.025*h 65 22]);
handles.AxesBackgroundColorPicker = uicontrol('Style','pushbutton',...
    'Parent',handles.AxesPanel, ...
    'CData',FloodFillIcon(),...
    'Tooltip', 'Choose Axes Background Color',...
    'Position',[0.83*w 0.135*h 20 20]);
handles.AxesFontColorPicker = uicontrol('Style','pushbutton',...
    'Parent',handles.AxesPanel, ...
    'CData',FontColorIcon(),...
    'Tooltip','Choose Axes Label Font Color',...
    'Position',[0.83*w 0.035*h 20 20]);
handles.ShowFileInfo = uicontrol('Style','checkbox', ...
    'Parent',handles.Figure, ...
    'String','Show data file information at bottom of figure?', ...
    'Units','pixels', ...
    'Position',[dx 0.32*h 400 22]);
handles.ApplyChanges = uicontrol('Style',   'pushbutton', ...
    'Parent',  handles.Figure, ...
    'String',  'Apply Changes', ...
    'Units',   'pixels', ...
    'Position',[0.6*w 0.22*h 100 22]);
handles.RestoreDefault = uicontrol('Style',   'pushbutton', ...
    'Parent',  handles.Figure, ...
    'String',  'Restore Default', ...
    'Units',   'pixels', ...
    'Position',[0.6*w 0.13*h 100 22]);
handles.Close = uicontrol('Style',   'pushbutton', ...
    'Parent',  handles.Figure, ...
    'String',  'Close', ...
    'Units',   'pixels', ...
    'Position',[0.6*w 0.04*h 100 22]);
setappdata(handles.Figure,'diagram_params',diagram_params)
setappdata(handles.Figure,'original_params',original_params)

% Set the callbacks:
% ------------------
set(handles.TitleString,                'Callback',{@TitleString_Callback,handles})
set(handles.TitleFont,                  'Callback',{@TitleFont_Callback,handles})
set(handles.TitleSize,                  'Callback',{@TitleSize_Callback,handles})
set(handles.PlotColorPicker,            'Callback',{@PlotColorPicker_Callback,handles})
set(handles.TitleColorPicker,           'Callback',{@TitleColorPicker_Callback,handles})
set(handles.AxesFont,                   'Callback',{@AxesFont_Callback,handles})
set(handles.AxesSize,                   'Callback',{@AxesSize_Callback,handles})
set(handles.AxesBackgroundColorPicker,  'Callback',{@AxesBackgroundColorPicker_Callback,handles})
set(handles.AxesFontColorPicker,        'Callback',{@AxesFontColorPicker_Callback,handles})
set(handles.AxesGridOnOff,              'Callback',{@AxesGridOnOff_Callback,handles})
set(handles.ShowFileInfo,               'Callback',{@ShowFileInfo_Callback,handles})

set(handles.ApplyChanges,               'Callback',{@ApplyChanges_Callback,handles})
set(handles.RestoreDefault,             'Callback',{@RestoreDefault_Callback,handles})
set(handles.Close,                      'Callback',{@Close_Callback,handles})

% Position the dialog and make it visible:
% ----------------------------------------
fpos = get(figure_handle,'Position');
dpos = [fpos(1)+fpos(3)+dx fpos(2)];
movegui(handles.Figure,dpos)
set(handles.Figure,...
    'Visible','on',...
    'WindowStyle','normal')

% Callback functions
% ------------------
function PlotColorPicker_Callback(hObject,eventdata,handles)
diagram_params = getappdata(handles.Figure,'diagram_params');
the_new_color = uisetcolor(diagram_params.plot_color);

diagram_params.plot_color = the_new_color;
setappdata(handles.Figure,'diagram_params',diagram_params)

function TitleString_Callback(hObject,eventdata,handles)
diagram_params = getappdata(handles.Figure,'diagram_params');
the_new_string = get(handles.TitleString,'String');

diagram_params.title_string = the_new_string;
setappdata(handles.Figure,'diagram_params',diagram_params)

function TitleFont_Callback(hObject,eventdata,handles)
diagram_params = getappdata(handles.Figure,'diagram_params');
the_new_font = diagram_params.all_fonts{get(handles.TitleFont,'Value')};

diagram_params.title_font = the_new_font;
setappdata(handles.Figure,'diagram_params',diagram_params)

function TitleSize_Callback(hObject,eventdata,handles)
diagram_params = getappdata(handles.Figure,'diagram_params');
the_new_size = str2num(get(handles.TitleSize,'String'));

diagram_params.title_size = the_new_size;
setappdata(handles.Figure,'diagram_params',diagram_params)

function TitleColorPicker_Callback(hObject,eventdata,handles)
diagram_params = getappdata(handles.Figure,'diagram_params');
the_new_color = uisetcolor(diagram_params.title_color);

diagram_params.title_color = the_new_color;
setappdata(handles.Figure,'diagram_params',diagram_params)

function AxesFont_Callback(hObject,eventdata,handles)
diagram_params = getappdata(handles.Figure,'diagram_params');
the_new_font = diagram_params.all_fonts{get(handles.AxesFont,'Value')};

diagram_params.axes_font = the_new_font;
setappdata(handles.Figure,'diagram_params',diagram_params)

function AxesSize_Callback(hObject,eventdata,handles)
diagram_params = getappdata(handles.Figure,'diagram_params');
the_new_size = str2num(get(handles.AxesSize,'String'));

diagram_params.axes_size = the_new_size;
setappdata(handles.Figure,'diagram_params',diagram_params)

function AxesBackgroundColorPicker_Callback(hObject,eventdata,handles)
diagram_params = getappdata(handles.Figure,'diagram_params');
the_new_color = uisetcolor(diagram_params.axes_color);

diagram_params.axes_color = the_new_color;
setappdata(handles.Figure,'diagram_params',diagram_params)

function AxesFontColorPicker_Callback(hObject,eventdata,handles)
diagram_params = getappdata(handles.Figure,'diagram_params');
the_new_color = uisetcolor(diagram_params.axes_font_color);

diagram_params.axes_font_color = the_new_color;
setappdata(handles.Figure,'diagram_params',diagram_params)

function AxesGridOnOff_Callback(hObject,eventdata,handles)
diagram_params = getappdata(handles.Figure,'diagram_params');
grid_oo = get(handles.AxesGridOnOff,'Value');
switch grid_oo
    case 0
        diagram_params.axes_grid = 'off';
    case 1
        diagram_params.axes_grid = 'on';
end
setappdata(handles.Figure,'diagram_params',diagram_params)

function ApplyChanges_Callback(hObject,eventdata,handles)
diagram_params = getappdata(handles.Figure,'diagram_params');

figure_handle = diagram_params.figure_handle;
figure(figure_handle)
axes_handle = gca;
hxLabel = get(axes_handle,'xLabel');
hyLabel = get(axes_handle,'yLabel');
title_handle = get(gca,'title');
hfileinfo = findobj('tag','fileinfotxt');
hcolorbar = findobj('tag','Colorbar');

% Update the figure
set(figure_handle,'Color',diagram_params.plot_color);
set(title_handle,'String',diagram_params.title_string);
set(title_handle,'FontName',diagram_params.title_font);
set(title_handle,'FontSize',diagram_params.title_size);
set(title_handle,'Color',diagram_params.title_color);
set(axes_handle,'FontName',diagram_params.axes_font);
set(axes_handle,'FontSize',diagram_params.axes_size);
set(axes_handle,'Color',diagram_params.axes_color);
set(axes_handle,...
    'XColor',diagram_params.axes_font_color,...
    'YColor',diagram_params.axes_font_color,...
    'ZColor',diagram_params.axes_font_color,...
    'XGrid',  diagram_params.axes_grid,...
    'YGrid',  diagram_params.axes_grid);
set(hcolorbar,'YColor',diagram_params.axes_font_color);
set(hfileinfo,...
    'BackgroundColor',diagram_params.plot_color,...
    'ForegroundColor',diagram_params.axes_font_color,...
    'FontName',diagram_params.axes_font)
set(hxLabel,...
    'Color', diagram_params.axes_font_color,...
    'FontName',diagram_params.axes_font);
set(hyLabel,...
    'Color', diagram_params.axes_font_color,...
    'FontName',diagram_params.axes_font);
set(hfileinfo, 'Visible',diagram_params.show_file_info)

function ShowFileInfo_Callback(hObject,eventdata,handles)
diagram_params = getappdata(handles.Figure,'diagram_params');
show_oo = get(handles.ShowFileInfo,'Value');
switch show_oo
    case 0
        diagram_params.show_file_info = 'off';
    case 1
        diagram_params.show_file_info = 'on';
end
setappdata(handles.Figure,'diagram_params',diagram_params)

function RestoreDefault_Callback(hObject,eventdata,handles)
original_params = getappdata(handles.Figure,'original_params');

figure_handle = original_params.figure_handle;
figure(figure_handle)
axes_handle = gca;
hxLabel = get(axes_handle,'xLabel');
hyLabel = get(axes_handle,'yLabel');
title_handle = get(gca,'title');
hfileinfo = findobj('tag','fileinfotxt');
hcolorbar = findobj('tag','Colorbar');

% Update the figure
set(figure_handle,'Color',original_params.plot_color);
set(title_handle,'String',original_params.title_string);
set(title_handle,'FontName',original_params.title_font);
set(title_handle,'FontSize',original_params.title_size);
set(title_handle,'Color',original_params.title_color);
set(axes_handle,'FontName',original_params.axes_font);
set(axes_handle,'FontSize',original_params.axes_size);
set(axes_handle,'Color',original_params.axes_color);
set(axes_handle,...
    'XColor',original_params.axes_font_color,...
    'YColor',original_params.axes_font_color,...
    'ZColor',original_params.axes_font_color,...
    'XGrid',  original_params.axes_grid,...
    'YGrid',  original_params.axes_grid);
set(hcolorbar,'YColor',original_params.axes_font_color);
set(hfileinfo,...
    'BackgroundColor',original_params.plot_color,...
    'ForegroundColor',original_params.axes_font_color,...
    'FontName',original_params.axes_font,...
    'Visible','off')
set(hxLabel,...
    'Color', original_params.axes_font_color,...
    'FontName',original_params.axes_font);
set(hyLabel,...
    'Color', original_params.axes_font_color,...
    'FontName',original_params.axes_font);

% Update the dialog
set(handles.TitleString,                'String',original_params.title_string)
set(handles.TitleFont,                  'Value',original_params.selected_font_idx)
set(handles.TitleSize,                  'String',original_params.title_size)
set(handles.AxesFont,                   'Value',original_params.selected_font_idx)
set(handles.AxesSize,                   'String',original_params.axes_size)
set(handles.AxesGridOnOff,              'Value',0)
set(handles.ShowFileInfo,               'Value',0)

function Close_Callback(hObject,eventdata,handles)
diagram_params = getappdata(handles.Figure,'diagram_params');
closereq;


